import React, { Component } from 'react';
import { List } from 'antd';
import { Album } from './Album';
import { Tracks } from '../Tracks';
import PropTypes from 'prop-types';
import DataService from '../../services/DataService';
import 'antd/dist/antd.css';

class AlbumContainer extends Component {
    constructor(props){
        super(props);
        this.state = {
            albumItem: {}
        }
    }

    onClick = async (item) => {
        console.log(item)
        const album = await DataService.chooseAlbum(item.trackList);
        this.setState({albumItem: album})
    }
   render(){
       console.log(!Object.is(this.state.albumItem, {}) )
    return(
    <div>
        <List
             grid={{ gutter: 16, column: 5}}
             dataSource={this.props.artistsData}
             renderItem={item => (
             <List.Item>
                 <Album onClick={() => this.onClick(item.album)} item={item.album}
                 />
             </List.Item>
            )}
        />
        { !Object.is(this.state.albumItem, {}) 
        && <Tracks data={this.state.albumItem} />} 
     </div>);
   }
}
export default AlbumContainer;
