import React, { Component } from 'react';
import { List } from 'antd';
import { secondsToHms } from '../../services/UtilsLinks'
import PropTypes from 'prop-types';
import './styles.css';

class Tracks extends Component {

    renderItem = (item) => {
        return (
            <ul>
                <li className="track">{item.track_position}</li>
                <li className="title">{item.title}</li>
                <li className="artist-name">{item.artist.name}</li>
                <li className="time">{secondsToHms(item.duration)}</li>
            </ul>
        );
    }
    renderHeader = () => {
        return (
            <ul>
                <li>#</li>
                <li>Title</li>
                <li>Artist</li>
                <li>Time</li>
            </ul>
        );
    }
    render(){
        return(
            <List
            header={this.renderHeader()}
            bordered
            dataSource={this.props.data}
            renderItem={item => (<List.Item>{this.renderItem(item)}</List.Item>)}
          />
        )
    }
}
export default Tracks;
